import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

    constructor(props) {
	super(props);
	this.state = {
	    error: null,
	    isLoaded: false,
	    items: []
	};
    }

//    state = {
//	hotdogs: []
//    }

    componentDidMount() {
	fetch("http://54.93.118.176:4000/hotdog")
	    .then(res => res.json())
	    .then(
		(res) => {
		    this.setState({ 
			isLoaded: true,
			items: res.data
		    });
		},
		(error) => {
		    this.setState({ 
			isLoaded: true,
			error
		    });
		}
	    )
    }

//    renderHotdog = ({ id, name}) => <div key={id}>{name}</div>

    render() {
	const { error, isLoaded, items } = this.state;
	if (error) {
	    return <div>Ошибка: {error.message}</div>;
	} else if (!isLoaded) {
	    return <div>Загрузка...</div>;
	} else {
	    return (
		<ul>
		    {items.map(item => (
			<li key={item.id}>
			    {item.name} {item.price}
			</li>
		    ))}
		</ul>
	    );
	}
    }

//    renderHotdog = ({ id, name }) => 

}

export default App;
