import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {

    constructor(props) {
	super(props);
	this.state = {
	    error: null,
	    isLoaded: false,
	    items: []
	};
    }

//    state = {
//	hotdogs: []
//    }

    componentDidMount() {
	fetch("http://localhost:4000/hotdog")
	    .then(response => response.json())
	    .then(
		(result) => {
		    this.setState({ 
			isLoaded: true,
			items: result.data
		    });
		},
		(error) => {
		    this.setState({ 
			isLoaded: true,
			error
		    });
		}
	    )
    }

//    renderHotdog = ({ id, name}) => <div key={id}>{name}</div>

    render() {
	const { error, isLoaded, items } = this.state;
	return (
	    <div className="App">
	    {items.map(item =>
		    <li key={item.id}>
			{item.name} {item.price}
		    </li>
		)}
	    </div>
	);
    }

//    renderHotdog = ({ id, name }) => 

}

export default App;
